$(document).ready(function(){var i=$(".submenu"),n=$(".mainmenu");i.hide(),i.first().delay(400).slideDown(700),i.on("click","li",function(){i.siblings().find("li").removeClass("chosen"),$(this).addClass("chosen")}),n.on("click","li",function(){$(this).next(".submenu").slideToggle().siblings(".submenu").slideUp()}),n.children("li:last-child").on("click",function(){n.fadeOut().delay(500).fadeIn()})});